contracts/authorizers/RequesterAuthorize.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "./interfaces/IRequesterAuthorizer.sol";

/// @title Abstract contract that temporarily or indefinitely authorizes
/// requesters for Airnodes
/// @dev Airnodes can be configured to use multiple Authorizers, and one of
/// them returning `true` means the request should be responded to. The Airnode
/// operator is expected to communicate the required information to the users
/// through off-chain channels.
abstract contract RequesterAuthorizer is IRequesterAuthorizer {
    struct AuthorizationStatus {
        uint32 expirationTimestamp;
        uint224 indefiniteAuthorizationCount;
    }

    /// @notice Authorization expiration extender role description
    string
        public constant
        override AUTHORIZATION_EXPIRATION_EXTENDER_ROLE_DESCRIPTION =
        "Authorization expiration extender";

    /// @notice Authorization expiration setter role description
    string
        public constant
        override AUTHORIZATION_EXPIRATION_SETTER_ROLE_DESCRIPTION =
        "Authorization expiration setter";

    /// @notice Indefinite authorizer role description
    string public constant override INDEFINITE_AUTHORIZER_ROLE_DESCRIPTION =
        "Indefinite authorizer";

    mapping(address => mapping(address => AuthorizationStatus))
        public
        override airnodeToRequesterToAuthorizationStatus;

    mapping(address => mapping(address => mapping(address => bool)))
        public
        override airnodeToRequesterToSetterToIndefiniteAuthorizationStatus;

    /// @notice Extends the expiration of the temporary authorization of
    /// the requester` for the Airnode
    /// @param airnode Airnode address
    /// @param requester Requester address
    /// @param expirationTimestamp Timestamp at which the temporary
    /// authorization will expire
    function _extendAuthorizationExpiration(
        address airnode,
        address requester,
        uint32 expirationTimestamp
    ) internal {
        require(airnode != address(0), "Airnode address zero");
        require(requester != address(0), "Requester address zero");
        require(
            expirationTimestamp >
                airnodeToRequesterToAuthorizationStatus[airnode][requester]
                    .expirationTimestamp,
            "Does not extend expiration"
        );
        airnodeToRequesterToAuthorizationStatus[airnode][requester]
            .expirationTimestamp = expirationTimestamp;
        emit ExtendedAuthorizationExpiration(
            airnode,
            requester,
            expirationTimestamp,
            _msgSender()
        );
    }

    /// @notice Sets the expiration of the temporary authorization of
    /// the requester for  the Airnode
    /// @dev Unlike `_extendAuthorizerExpiration()`, this can hasten expiration
    /// @param airnode Airnode address
    /// @param requester Requester address
    /// @param expirationTimestamp Timestamp at which the temporary
    /// authorization will expire
    function _setAuthorizationExpiration(
        address airnode,
        address requester,
        uint32 expirationTimestamp
    ) internal {
        require(airnode != address(0), "Airnode address zero");
        require(requester != address(0), "Requester address zero");
        airnodeToRequesterToAuthorizationStatus[airnode][requester]
            .expirationTimestamp = expirationTimestamp;
        emit SetAuthorizationExpiration(
            airnode,
            requester,
            expirationTimestamp,
            _msgSender()
        );
    }

    /// @notice Sets the indefinite authorization status of the requester for
    /// the Airnode
    /// @dev Emits the event even if it does not change the state
    /// @param airnode Airnode address
    /// @param requester Requester address
    /// @param status Indefinite authorization status
    function _setIndefiniteAuthorizationStatus(
        address airnode,
        address requester,
        bool status
    ) internal {
        require(airnode != address(0), "Airnode address zero");
        require(requester != address(0), "Requester address zero");
        uint224 indefiniteAuthorizationCount = airnodeToRequesterToAuthorizationStatus[
                airnode
            ][requester].indefiniteAuthorizationCount;
        if (
            status &&
            !airnodeToRequesterToSetterToIndefiniteAuthorizationStatus[airnode][
                requester
            ][_msgSender()]
        ) {
            airnodeToRequesterToSetterToIndefiniteAuthorizationStatus[airnode][
                requester
            ][_msgSender()] = true;
            unchecked {
                indefiniteAuthorizationCount++;
            }
            airnodeToRequesterToAuthorizationStatus[airnode][requester]
                .indefiniteAuthorizationCount = indefiniteAuthorizationCount;
        } else if (
            !status &&
            airnodeToRequesterToSetterToIndefiniteAuthorizationStatus[airnode][
                requester
            ][_msgSender()]
        ) {
            airnodeToRequesterToSetterToIndefiniteAuthorizationStatus[airnode][
                requester
            ][_msgSender()] = false;
            unchecked {
                indefiniteAuthorizationCount--;
            }
            airnodeToRequesterToAuthorizationStatus[airnode][requester]
                .indefiniteAuthorizationCount = indefiniteAuthorizationCount;
        }
        emit SetIndefiniteAuthorizationStatus(
            airnode,
            requester,
            status,
            indefiniteAuthorizationCount,
            _msgSender()
        );
    }

    /// @notice Revokes the indefinite authorization status granted to the
    /// requester for the Airnode by a specific account
    /// @dev Only emits the event if it changes the state
    /// @param airnode Airnode address
    /// @param requester Requester address
    /// @param setter Setter of the indefinite authorization status
    function _revokeIndefiniteAuthorizationStatus(
        address airnode,
        address requester,
        address setter
    ) internal {
        require(airnode != address(0), "Airnode address zero");
        require(requester != address(0), "Requester address zero");
        require(setter != address(0), "Setter address zero");
        uint224 indefiniteAuthorizationCount = airnodeToRequesterToAuthorizationStatus[
                airnode
            ][requester].indefiniteAuthorizationCount;
        if (
            airnodeToRequesterToSetterToIndefiniteAuthorizationStatus[airnode][
                requester
            ][setter]
        ) {
            airnodeToRequesterToSetterToIndefiniteAuthorizationStatus[airnode][
                requester
            ][setter] = false;
            unchecked {
                indefiniteAuthorizationCount--;
            }
            airnodeToRequesterToAuthorizationStatus[airnode][requester]
                .indefiniteAuthorizationCount = indefiniteAuthorizationCount;
            emit RevokedIndefiniteAuthorizationStatus(
                airnode,
                requester,
                setter,
                indefiniteAuthorizationCount,
                _msgSender()
            );
        }
    }

    /// @notice Verifies the authorization status of the requester for the
    /// Airnode
    /// @param airnode Airnode address
    /// @param requester Requester address
    /// @return Authorization status of the request
    function isAuthorized(
        address airnode,
        address requester
    ) public view override returns (bool) {
        AuthorizationStatus
            storage authorizationStatus = airnodeToRequesterToAuthorizationStatus[
                airnode
            ][requester];
        return
            authorizationStatus.indefiniteAuthorizationCount > 0 ||
            authorizationStatus.expirationTimestamp > block.timestamp;
    }

    /// @dev See Context.sol
    function _msgSender() internal view virtual returns (address sender);
}

// SPDX-License-Identifier: MIT
pragma solidity 0.8.17;

import "@openzeppelin/contracts/metatx/ERC2771Context.sol";
import "../access-control-registry/AccessControlRegistryAdminned.sol";
import "./RequesterAuthorizer.sol";
import "./interfaces/IRequesterAuthorizerWithAirnode.sol";

/// @title Authorizer contract that Airnode operators can use to temporarily or
/// indefinitely authorize requesters for the respective Airnodes
contract RequesterAuthorizerWithAirnode is
    ERC2771Context,
    AccessControlRegistryAdminned,
    RequesterAuthorizer,
    IRequesterAuthorizerWithAirnode
{
    bytes32
        private constant AUTHORIZATION_EXPIRATION_EXTENDER_ROLE_DESCRIPTION_HASH =
        keccak256(
            abi.encodePacked(AUTHORIZATION_EXPIRATION_EXTENDER_ROLE_DESCRIPTION)
        );

    bytes32
        private constant AUTHORIZATION_EXPIRATION_SETTER_ROLE_DESCRIPTION_HASH =
        keccak256(
            abi.encodePacked(AUTHORIZATION_EXPIRATION_SETTER_ROLE_DESCRIPTION)
        );

    bytes32 private constant INDEFINITE_AUTHORIZER_ROLE_DESCRIPTION_HASH =
        keccak256(abi.encodePacked(INDEFINITE_AUTHORIZER_ROLE_DESCRIPTION));

    /// @param _accessControlRegistry AccessControlRegistry contract address
    /// @param _adminRoleDescription Admin role description
    constructor(
        address _accessControlRegistry,
        string memory _adminRoleDescription
    )
        ERC2771Context(_accessControlRegistry)
        AccessControlRegistryAdminned(
            _accessControlRegistry,
            _adminRoleDescription
        )
    {}

    /// @notice Extends the expiration of the temporary authorization of
    /// `requester` for `airnode` if the sender is allowed to extend
    /// authorization expiration
    /// @param airnode Airnode address
    /// @param requester Requester address
    /// @param expirationTimestamp Timestamp at which the temporary
    /// authorization will expire
    function extendAuthorizerExpiration(
        address airnode,
        address requester,
        uint32 expirationTimestamp
    ) external override {
        require(
            hasAuthorizationExpirationExtenderRoleOrIsAirnode(
                airnode,
                _msgSender()
            ),
            "Cannot extend expiration"
        );
        _extendAuthorizationExpiration(airnode, requester, expirationTimestamp);
    }

    /// @notice Sets the expiration of the temporary authorization of
    /// `requester` for `airnode` if the sender is allowed to set expiration
    /// @dev Unlike `extendAuthorizerExpiration()`, this can hasten expiration
    /// @param airnode Airnode address
    /// @param requester Requester address
    /// @param expirationTimestamp Timestamp at which the temporary
    /// authorization will expire
    function setAuthorizationExpiration(
        address airnode,
        address requester,
        uint32 expirationTimestamp
    ) external override {
        require(
            hasAuthorizationExpirationSetterRoleOrIsAirnode(
                airnode,
                _msgSender()
            ),
            "Cannot set expiration"
        );
        _setAuthorizationExpiration(airnode, requester, expirationTimestamp);
    }

    /// @notice Sets the indefinite authorization status of `requester` for
    /// `airnode` if the sender is allowed to authorize indefinitely
    /// @param airnode Airnode address
    /// @param requester Requester address
    /// @param status Indefinite authorization status
    function setIndefiniteAuthorizationStatus(
        address airnode,
        address requester,
        bool status
    ) external override {
        require(
            hasIndefiniteAuthorizerRoleOrIsAirnode(airnode, _msgSender()),
            "Cannot set indefinite status"
        );
        _setIndefiniteAuthorizationStatus(airnode, requester, status);
    }

    /// @notice Revokes the indefinite authorization status granted by a
    /// specific account that no longer has the indefinite authorizer role
    /// @param airnode Airnode address
    /// @param requester Requester address
    /// @param setter Setter of the indefinite authorization status
    function revokeIndefiniteAuthorizationStatus(
        address airnode,
        address requester,
        address setter
    ) external override {
        require(
            !hasIndefiniteAuthorizerRoleOrIsAirnode(airnode, setter),
            "setter can set indefinite status"
        );
        _revokeIndefiniteAuthorizationStatus(airnode, requester, setter);
    }

    /// @notice Derives the admin role for the Airnode
    /// @param airnode Airnode address
    /// @return adminRole Admin role
    function deriveAdminRole(
        address airnode
    ) external view override returns (bytes32 adminRole) {
        adminRole = _deriveAdminRole(airnode);
    }

    /// @notice Derives the authorization expiration extender role for the
    /// Airnode
    /// @param airnode Airnode address
    /// @return authorizationExpirationExtenderRole Authorization expiration
    /// extender role
    function deriveAuthorizationExpirationExtenderRole(
        address airnode
    )
        public
        view
        override
        returns (bytes32 authorizationExpirationExtenderRole)
    {
        authorizationExpirationExtenderRole = _deriveRole(
            _deriveAdminRole(airnode),
            AUTHORIZATION_EXPIRATION_EXTENDER_ROLE_DESCRIPTION_HASH
        );
    }

    /// @notice Derives the authorization expiration setter role for the
    /// Airnode
    /// @param airnode Airnode address
    /// @return authorizationExpirationSetterRole Authorization expiration
    /// setter role
    function deriveAuthorizationExpirationSetterRole(
        address airnode
    ) public view override returns (bytes32 authorizationExpirationSetterRole) {
        authorizationExpirationSetterRole = _deriveRole(
            _deriveAdminRole(airnode),
            AUTHORIZATION_EXPIRATION_SETTER_ROLE_DESCRIPTION_HASH
        );
    }

    /// @notice Derives the indefinite authorizer role for the Airnode
    /// @param airnode Airnode address
    /// @return indefiniteAuthorizerRole Indefinite authorizer role
    function deriveIndefiniteAuthorizerRole(
        address airnode
    ) public view override returns (bytes32 indefiniteAuthorizerRole) {
        indefiniteAuthorizerRole = _deriveRole(
            _deriveAdminRole(airnode),
            INDEFINITE_AUTHORIZER_ROLE_DESCRIPTION_HASH
        );
    }

    /// @dev Returns if the account has the authorization expiration extender
    /// role or is the Airnode address
    /// @param airnode Airnode address
    /// @param account Account address
    /// @return If the account has the authorization extender role or is the
    /// Airnode address
    function hasAuthorizationExpirationExtenderRoleOrIsAirnode(
        address airnode,
        address account
    ) private view returns (bool) {
        return
            airnode == account ||
            IAccessControlRegistry(accessControlRegistry).hasRole(
                deriveAuthorizationExpirationExtenderRole(airnode),
                account
            );
    }

    /// @dev Returns if the account has the authorization expriation setter
    /// role or is the Airnode address
    /// @param airnode Airnode address
    /// @param account Account address
    /// @return If the account has the authorization expiration setter role or
    /// is the Airnode address
    function hasAuthorizationExpirationSetterRoleOrIsAirnode(
        address airnode,
        address account
    ) private view returns (bool) {
        return
            airnode == account ||
            IAccessControlRegistry(accessControlRegistry).hasRole(
                deriveAuthorizationExpirationSetterRole(airnode),
                account
            );
    }

    /// @dev Returns if the account has the indefinite authorizer role or is
    /// the Airnode address
    /// @param airnode Airnode address
    /// @param account Account address
    /// @return If the account has the indefinite authorizer role or is the
    /// Airnode addrss
    function hasIndefiniteAuthorizerRoleOrIsAirnode(
        address airnode,
        address account
    ) private view returns (bool) {
        return
            airnode == account ||
            IAccessControlRegistry(accessControlRegistry).hasRole(
                deriveIndefiniteAuthorizerRole(airnode),
                account
            );
    }

    /// @dev See Context.sol
    function _msgSender()
        internal
        view
        virtual
        override(RequesterAuthorizer, ERC2771Context)
        returns (address)
    {
        return ERC2771Context._msgSender();
    }
}

// SPDX-License-Identifier: MIT
pragma solidity 0.8.17;

import "@openzeppelin/contracts/metatx/ERC2771Context.sol";
import "../access-control-registry/AccessControlRegistryAdminned.sol";
import "./interfaces/IRequesterAuthorizerWithErc721.sol";
import "@openzeppelin/contracts/utils/math/SafeCast.sol";
import "@openzeppelin/contracts/token/ERC721/IERC721.sol";

/// @title Authorizer contract that users can deposit the ERC721 tokens
/// recognized by the Airnode to receive authorization for the requester
/// contract on the chain
/// @notice For an Airnode to treat an ERC721 token deposit as a valid reason
/// for the respective requester contract to be authorized, it needs to be
/// configured at deploy-time to (1) use this contract as an authorizer,
/// (2) recognize the respectice ERC721 token contract.
/// It can be expected for Airnodes to be configured to only recognize the
/// respective NFT keys that their operators have issued, but this is not
/// necessarily true, i.e., an Airnode can be configured to recognize an
/// arbitrary ERC721 token.
/// This contract allows Airnodes to block specific requester contracts. It can
/// be expected for Airnodes to only do this when the requester is breaking
/// T&C. The tokens that have been deposited to authorize requesters that have
/// been blocked can be revoked, which transfers them to the Airnode account.
/// This can be seen as a staking/slashing mechanism. Accordingly, users should
/// not deposit ERC721 tokens to receive authorization from Airnodes that they
/// suspect may abuse this mechanic.
/// @dev Airnode operators are strongly recommended to only use a single
/// instance of this contract as an authorizer. If multiple instances are used,
/// the state between the instances should be kept consistent. For example, if
/// a requester on a chain is to be blocked, all instances of this contract
/// that are used as authorizers for the chain should be updated. Otherwise,
/// the requester to be blocked can still be authorized via the instances that
/// have not been updated.
contract RequesterAuthorizerWithErc721 is
    ERC2771Context,
    AccessControlRegistryAdminned,
    IRequesterAuthorizerWithErc721
{
    struct TokenDeposits {
        uint256 count;
        mapping(address => Deposit) depositorToDeposit;
    }

    struct Deposit {
        uint256 tokenId;
        uint32 withdrawalLeadTime;
        uint32 earliestWithdrawalTime;
        DepositState state;
    }

    /// @notice Withdrawal lead time setter role description
    string
        public constant
        override WITHDRAWAL_LEAD_TIME_SETTER_ROLE_DESCRIPTION =
        "Withdrawal lead time setter";
    /// @notice Requester blocker role description
    string public constant override REQUESTER_BLOCKER_ROLE_DESCRIPTION =
        "Requester blocker";
    /// @notice Depositor freezer role description
    string public constant override DEPOSITOR_FREEZER_ROLE_DESCRIPTION =
        "Depositor freezer";

    bytes32 private constant WITHDRAWAL_LEAD_TIME_SETTER_ROLE_DESCRIPTION_HASH =
        keccak256(
            abi.encodePacked(WITHDRAWAL_LEAD_TIME_SETTER_ROLE_DESCRIPTION)
        );
    bytes32 private constant REQUESTER_BLOCKER_ROLE_DESCRIPTION_HASH =
        keccak256(abi.encodePacked(REQUESTER_BLOCKER_ROLE_DESCRIPTION));
    bytes32 private constant DEPOSITOR_FREEZER_ROLE_DESCRIPTION_HASH =
        keccak256(abi.encodePacked(DEPOSITOR_FREEZER_ROLE_DESCRIPTION));

    /// @notice Deposits of the token with the address made for the Airnode to
    /// authorize the requester address on the chain
    mapping(address => mapping(uint256 => mapping(address => mapping(address => TokenDeposits))))
        public
        override airnodeToChainIdToRequesterToTokenAddressToTokenDeposits;

    /// @notice Withdrawal lead time of the Airnode. This creates the window of
    /// opportunity during which a requester can be blocked for breaking T&C
    /// and the respective token can be revoked.
    /// The withdrawal lead time at deposit-time will apply to a specific
    /// deposit.
    mapping(address => uint32) public override airnodeToWithdrawalLeadTime;

    /// @notice If the Airnode has blocked the requester on the chain. In the
    /// context of the respective Airnode, no one can deposit for a blocked
    /// requester, make deposit updates that relate to a blocked requester, or
    /// withdraw a token deposited for a blocked requester. Anyone can revoke
    /// tokens that are already deposited for a blocked requester. Existing
    /// deposits for a blocked requester do not provide authorization.
    mapping(address => mapping(uint256 => mapping(address => bool)))
        public
        override airnodeToChainIdToRequesterToBlockStatus;

    /// @notice If the Airnode has frozen the depositor. In the context of the
    /// respective Airnode, a frozen depositor cannot deposit, make deposit
    /// updates or withdraw.
    mapping(address => mapping(address => bool))
        public
        override airnodeToDepositorToFreezeStatus;

    /// @param _accessControlRegistry AccessControlRegistry contract address
    /// @param _adminRoleDescription Admin role description
    constructor(
        address _accessControlRegistry,
        string memory _adminRoleDescription
    )
        ERC2771Context(_accessControlRegistry)
        AccessControlRegistryAdminned(
            _accessControlRegistry,
            _adminRoleDescription
        )
    {}

    /// @notice Called by the Airnode or its withdrawal lead time setters to
    /// set withdrawal lead time
    /// @param airnode Airnode address
    /// @param withdrawalLeadTime Withdrawal lead time
    function setWithdrawalLeadTime(
        address airnode,
        uint32 withdrawalLeadTime
    ) external override {
        require(
            airnode == _msgSender() ||
                IAccessControlRegistry(accessControlRegistry).hasRole(
                    deriveWithdrawalLeadTimeSetterRole(airnode),
                    _msgSender()
                ),
            "Sender cannot set lead time"
        );
        require(withdrawalLeadTime <= 30 days, "Lead time too long");
        airnodeToWithdrawalLeadTime[airnode] = withdrawalLeadTime;
        emit SetWithdrawalLeadTime(airnode, withdrawalLeadTime, _msgSender());
    }

    /// @notice Called by the Airnode or its requester blockers to set
    /// the block status of the requester
    /// @param airnode Airnode address
    /// @param chainId Chain ID
    /// @param requester Requester address
    /// @param status Block status
    function setRequesterBlockStatus(
        address airnode,
        uint256 chainId,
        address requester,
        bool status
    ) external override {
        require(
            airnode == _msgSender() ||
                IAccessControlRegistry(accessControlRegistry).hasRole(
                    deriveRequesterBlockerRole(airnode),
                    _msgSender()
                ),
            "Sender cannot block requester"
        );
        require(chainId != 0, "Chain ID zero");
        require(requester != address(0), "Requester address zero");
        airnodeToChainIdToRequesterToBlockStatus[airnode][chainId][
            requester
        ] = status;
        emit SetRequesterBlockStatus(
            airnode,
            requester,
            chainId,
            status,
            _msgSender()
        );
    }

    /// @notice Called by the Airnode or its depositor freezers to set the
    /// freeze status of the depositor
    /// @param airnode Airnode address
    /// @param depositor Depositor address
    /// @param status Freeze status
    function setDepositorFreezeStatus(
        address airnode,
        address depositor,
        bool status
    ) external override {
        require(
            airnode == _msgSender() ||
                IAccessControlRegistry(accessControlRegistry).hasRole(
                    deriveDepositorFreezerRole(airnode),
                    _msgSender()
                ),
            "Sender cannot freeze depositor"
        );
        require(depositor != address(0), "Depositor address zero");
        airnodeToDepositorToFreezeStatus[airnode][depositor] = status;
        emit SetDepositorFreezeStatus(airnode, depositor, status, _msgSender());
    }

    /// @notice Called by the ERC721 contract upon `safeTransferFrom()` to this
    /// contract to deposit a token to authorize the requester
    /// @dev The first argument is the operator, which we do not need
    /// @param _from Account from which the token is transferred
    /// @param _tokenId Token ID
    /// @param _data Airnode address, chain ID and requester address in
    /// ABI-encoded form
    /// @return `onERC721Received()` function selector
    function onERC721Received(
        address,
        address _from,
        uint256 _tokenId,
        bytes calldata _data
    ) external override returns (bytes4) {
        require(_data.length == 96, "Unexpected data length");
        (address airnode, uint256 chainId, address requester) = abi.decode(
            _data,
            (address, uint256, address)
        );
        require(airnode != address(0), "Airnode address zero");
        require(chainId != 0, "Chain ID zero");
        require(requester != address(0), "Requester address zero");
        require(
            !airnodeToChainIdToRequesterToBlockStatus[airnode][chainId][
                requester
            ],
            "Requester blocked"
        );
        require(
            !airnodeToDepositorToFreezeStatus[airnode][_from],
            "Depositor frozen"
        );
        TokenDeposits
            storage tokenDeposits = airnodeToChainIdToRequesterToTokenAddressToTokenDeposits[
                airnode
            ][chainId][requester][_msgSender()];
        uint256 tokenDepositCount;
        unchecked {
            tokenDepositCount = ++tokenDeposits.count;
        }
        require(
            tokenDeposits.depositorToDeposit[_from].state ==
                DepositState.Inactive,
            "Token already deposited"
        );
        tokenDeposits.depositorToDeposit[_from] = Deposit({
            tokenId: _tokenId,
            withdrawalLeadTime: airnodeToWithdrawalLeadTime[airnode],
            earliestWithdrawalTime: 0,
            state: DepositState.Active
        });
        emit DepositedToken(
            airnode,
            requester,
            _from,
            chainId,
            _msgSender(),
            _tokenId,
            tokenDepositCount
        );
        return this.onERC721Received.selector;
    }

    /// @notice Called by a token depositor to update the requester for which
    /// they have deposited the token for
    /// @dev This is especially useful for not having to wait when the Airnode
    /// has set a non-zero withdrawal lead time
    /// @param airnode Airnode address
    /// @param chainIdPrevious Previous chain ID
    /// @param requesterPrevious Previous requester address
    /// @param chainIdNext Next chain ID
    /// @param requesterNext Next requester address
    /// @param token Token address
    function updateDepositRequester(
        address airnode,
        uint256 chainIdPrevious,
        address requesterPrevious,
        uint256 chainIdNext,
        address requesterNext,
        address token
    ) external override {
        require(chainIdNext != 0, "Chain ID zero");
        require(requesterNext != address(0), "Requester address zero");
        require(
            !(chainIdPrevious == chainIdNext &&
                requesterPrevious == requesterNext),
            "Does not update requester"
        );
        require(
            !airnodeToChainIdToRequesterToBlockStatus[airnode][chainIdPrevious][
                requesterPrevious
            ],
            "Previous requester blocked"
        );
        require(
            !airnodeToChainIdToRequesterToBlockStatus[airnode][chainIdNext][
                requesterNext
            ],
            "Next requester blocked"
        );
        require(
            !airnodeToDepositorToFreezeStatus[airnode][_msgSender()],
            "Depositor frozen"
        );
        TokenDeposits
            storage requesterPreviousTokenDeposits = airnodeToChainIdToRequesterToTokenAddressToTokenDeposits[
                airnode
            ][chainIdPrevious][requesterPrevious][token];
        Deposit
            storage requesterPreviousDeposit = requesterPreviousTokenDeposits
                .depositorToDeposit[_msgSender()];
        if (requesterPreviousDeposit.state != DepositState.Active) {
            if (requesterPreviousDeposit.state == DepositState.Inactive) {
                revert("Token not deposited");
            } else {
                revert("Withdrawal initiated");
            }
        }
        TokenDeposits
            storage requesterNextTokenDeposits = airnodeToChainIdToRequesterToTokenAddressToTokenDeposits[
                airnode
            ][chainIdNext][requesterNext][token];
        require(
            requesterNextTokenDeposits.depositorToDeposit[_msgSender()].state ==
                DepositState.Inactive,
            "Token already deposited"
        );
        uint256 requesterNextTokenDepositCount = ++requesterNextTokenDeposits
            .count;
        requesterNextTokenDeposits.count = requesterNextTokenDepositCount;
        uint256 requesterPreviousTokenDepositCount = --requesterPreviousTokenDeposits
                .count;
        requesterPreviousTokenDeposits
            .count = requesterPreviousTokenDepositCount;
        uint256 tokenId = requesterPreviousDeposit.tokenId;
        requesterNextTokenDeposits.depositorToDeposit[_msgSender()] = Deposit({
            tokenId: tokenId,
            withdrawalLeadTime: requesterPreviousDeposit.withdrawalLeadTime,
            earliestWithdrawalTime: 0,
            state: DepositState.Active
        });
        requesterPreviousTokenDeposits.depositorToDeposit[
            _msgSender()
        ] = Deposit({
            tokenId: 0,
            withdrawalLeadTime: 0,
            earliestWithdrawalTime: 0,
            state: DepositState.Inactive
        });
        emit UpdatedDepositRequesterTo(
            airnode,
            requesterNext,
            _msgSender(),
            chainIdNext,
            token,
            tokenId,
            requesterNextTokenDepositCount
        );
        emit UpdatedDepositRequesterFrom(
            airnode,
            requesterPrevious,
            _msgSender(),
            chainIdPrevious,
            token,
            tokenId,
            requesterPreviousTokenDepositCount
        );
    }

    /// @notice Called by a token depositor to initiate withdrawal
    /// @dev The depositor is allowed to initiate a withdrawal even if the
    /// respective requester is blocked. However, the withdrawal will not be
    /// executable as long as the requester is blocked.
    /// Token withdrawals can be initiated even if withdrawal lead time is
    /// zero.
    /// @param airnode Airnode address
    /// @param chainId Chain ID
    /// @param requester Requester address
    /// @param token Token address
    /// @return earliestWithdrawalTime Earliest withdrawal time
    function initiateTokenWithdrawal(
        address airnode,
        uint256 chainId,
        address requester,
        address token
    ) external override returns (uint32 earliestWithdrawalTime) {
        TokenDeposits
            storage tokenDeposits = airnodeToChainIdToRequesterToTokenAddressToTokenDeposits[
                airnode
            ][chainId][requester][token];
        Deposit storage deposit = tokenDeposits.depositorToDeposit[
            _msgSender()
        ];
        if (deposit.state != DepositState.Active) {
            if (deposit.state == DepositState.Inactive) {
                revert("Token not deposited");
            } else {
                revert("Withdrawal already initiated");
            }
        }
        uint256 tokenDepositCount;
        unchecked {
            tokenDepositCount = --tokenDeposits.count;
        }
        earliestWithdrawalTime = SafeCast.toUint32(
            block.timestamp + deposit.withdrawalLeadTime
        );
        deposit.earliestWithdrawalTime = earliestWithdrawalTime;
        deposit.state = DepositState.WithdrawalInitiated;
        emit InitiatedTokenWithdrawal(
            airnode,
            requester,
            _msgSender(),
            chainId,
            token,
            deposit.tokenId,
            earliestWithdrawalTime,
            tokenDepositCount
        );
    }

    /// @notice Called by a token depositor to withdraw
    /// @param airnode Airnode address
    /// @param chainId Chain ID
    /// @param requester Requester address
    /// @param token Token address
    function withdrawToken(
        address airnode,
        uint256 chainId,
        address requester,
        address token
    ) external override {
        require(
            !airnodeToChainIdToRequesterToBlockStatus[airnode][chainId][
                requester
            ],
            "Requester blocked"
        );
        require(
            !airnodeToDepositorToFreezeStatus[airnode][_msgSender()],
            "Depositor frozen"
        );
        TokenDeposits
            storage tokenDeposits = airnodeToChainIdToRequesterToTokenAddressToTokenDeposits[
                airnode
            ][chainId][requester][token];
        Deposit storage deposit = tokenDeposits.depositorToDeposit[
            _msgSender()
        ];
        require(deposit.state != DepositState.Inactive, "Token not deposited");
        uint256 tokenDepositCount;
        if (deposit.state == DepositState.Active) {
            require(
                deposit.withdrawalLeadTime == 0,
                "Withdrawal not initiated"
            );
            unchecked {
                tokenDepositCount = --tokenDeposits.count;
            }
        } else {
            require(
                block.timestamp >= deposit.earliestWithdrawalTime,
                "Cannot withdraw yet"
            );
            unchecked {
                tokenDepositCount = tokenDeposits.count;
            }
        }
        uint256 tokenId = deposit.tokenId;
        tokenDeposits.depositorToDeposit[_msgSender()] = Deposit({
            tokenId: 0,
            withdrawalLeadTime: 0,
            earliestWithdrawalTime: 0,
            state: DepositState.Inactive
        });
        emit WithdrewToken(
            airnode,
            requester,
            _msgSender(),
            chainId,
            token,
            tokenId,
            tokenDepositCount
        );
        IERC721(token).safeTransferFrom(address(this), _msgSender(), tokenId);
    }

    /// @notice Called to revoke the token deposited to authorize a requester
    /// that is blocked now
    /// @param airnode Airnode address
    /// @param chainId Chain ID
    /// @param requester Requester address
    /// @param token Token address
    /// @param depositor Depositor address
    function revokeToken(
        address airnode,
        uint256 chainId,
        address requester,
        address token,
        address depositor
    ) external override {
        require(
            airnodeToChainIdToRequesterToBlockStatus[airnode][chainId][
                requester
            ],
            "Airnode did not block requester"
        );
        TokenDeposits
            storage tokenDeposits = airnodeToChainIdToRequesterToTokenAddressToTokenDeposits[
                airnode
            ][chainId][requester][token];
        Deposit storage deposit = tokenDeposits.depositorToDeposit[depositor];
        require(deposit.state != DepositState.Inactive, "Token not deposited");
        uint256 tokenDepositCount;
        if (deposit.state == DepositState.Active) {
            unchecked {
                tokenDepositCount = --tokenDeposits.count;
            }
        } else {
            unchecked {
                tokenDepositCount = tokenDeposits.count;
            }
        }
        uint256 tokenId = deposit.tokenId;
        tokenDeposits.depositorToDeposit[depositor] = Deposit({
            tokenId: 0,
            withdrawalLeadTime: 0,
            earliestWithdrawalTime: 0,
            state: DepositState.Inactive
        });
        emit RevokedToken(
            airnode,
            requester,
            depositor,
            chainId,
            token,
            tokenId,
            tokenDepositCount
        );
        IERC721(token).safeTransferFrom(address(this), airnode, tokenId);
    }

    /// @notice Returns the deposit of the token with the address made by the
    /// depositor for the Airnode to authorize the requester address on the
    /// chain
    /// @param airnode Airnode address
    /// @param chainId Chain ID
    /// @param requester Requester address
    /// @param token Token address
    /// @param depositor Depositor address
    /// @return tokenId Token ID
    /// @return withdrawalLeadTime Withdrawal lead time captured at
    /// deposit-time
    /// @return earliestWithdrawalTime Earliest withdrawal time
    function airnodeToChainIdToRequesterToTokenToDepositorToDeposit(
        address airnode,
        uint256 chainId,
        address requester,
        address token,
        address depositor
    )
        external
        view
        override
        returns (
            uint256 tokenId,
            uint32 withdrawalLeadTime,
            uint32 earliestWithdrawalTime,
            DepositState state
        )
    {
        Deposit
            storage deposit = airnodeToChainIdToRequesterToTokenAddressToTokenDeposits[
                airnode
            ][chainId][requester][token].depositorToDeposit[depositor];
        (tokenId, withdrawalLeadTime, earliestWithdrawalTime, state) = (
            deposit.tokenId,
            deposit.withdrawalLeadTime,
            deposit.earliestWithdrawalTime,
            deposit.state
        );
    }

    /// @notice Returns if the requester on the chain is authorized for the
    /// Airnode due to a token with the address being deposited
    /// @param airnode Airnode address
    /// @param chainId Chain ID
    /// @param requester Requester address
    /// @param token Token address
    /// @return Authorization status
    function isAuthorized(
        address airnode,
        uint256 chainId,
        address requester,
        address token
    ) external view override returns (bool) {
        return
            !airnodeToChainIdToRequesterToBlockStatus[airnode][chainId][
                requester
            ] &&
            airnodeToChainIdToRequesterToTokenAddressToTokenDeposits[airnode][
                chainId
            ][requester][token].count >
            0;
    }

    /// @notice Derives the withdrawal lead time setter role for the Airnode
    /// @param airnode Airnode address
    /// @return withdrawalLeadTimeSetterRole Withdrawal lead time setter role
    function deriveWithdrawalLeadTimeSetterRole(
        address airnode
    ) public view override returns (bytes32 withdrawalLeadTimeSetterRole) {
        withdrawalLeadTimeSetterRole = _deriveRole(
            _deriveAdminRole(airnode),
            WITHDRAWAL_LEAD_TIME_SETTER_ROLE_DESCRIPTION_HASH
        );
    }

    /// @notice Derives the requester blocker role for the Airnode
    /// @param airnode Airnode address
    /// @return requesterBlockerRole Requester blocker role
    function deriveRequesterBlockerRole(
        address airnode
    ) public view override returns (bytes32 requesterBlockerRole) {
        requesterBlockerRole = _deriveRole(
            _deriveAdminRole(airnode),
            REQUESTER_BLOCKER_ROLE_DESCRIPTION_HASH
        );
    }

    /// @notice Derives the depositor freezer role for the Airnode
    /// @param airnode Airnode address
    /// @return depositorFreezerRole Depositor freezer role
    function deriveDepositorFreezerRole(
        address airnode
    ) public view override returns (bytes32 depositorFreezerRole) {
        depositorFreezerRole = _deriveRole(
            _deriveAdminRole(airnode),
            DEPOSITOR_FREEZER_ROLE_DESCRIPTION_HASH
        );
    }
}

// SPDX-License-Identifier: MIT
pragma solidity 0.8.17;

import "@openzeppelin/contracts/metatx/ERC2771Context.sol";
import "../access-control-registry/AccessControlRegistryAdminnedWithManager.sol";
import "./RequesterAuthorizer.sol";
import "./interfaces/IRequesterAuthorizerWithManager.sol";

/// @title Authorizer contract that the manager can use to temporarily or
/// indefinitely authorize requesters for Airnodes
contract RequesterAuthorizerWithManager is
    ERC2771Context,
    AccessControlRegistryAdminnedWithManager,
    RequesterAuthorizer,
    IRequesterAuthorizerWithManager
{
    /// @notice Authorization expiration extender role
    bytes32 public immutable override authorizationExpirationExtenderRole;

    /// @notice Authorization expiration setter role
    bytes32 public immutable override authorizationExpirationSetterRole;

    /// @notice Indefinite authorizer role
    bytes32 public immutable override indefiniteAuthorizerRole;

    /// @param _accessControlRegistry AccessControlRegistry contract address
    /// @param _adminRoleDescription Admin role description
    /// @param _manager Manager address
    constructor(
        address _accessControlRegistry,
        string memory _adminRoleDescription,
        address _manager
    )
        ERC2771Context(_accessControlRegistry)
        AccessControlRegistryAdminnedWithManager(
            _accessControlRegistry,
            _adminRoleDescription,
            _manager
        )
    {
        authorizationExpirationExtenderRole = _deriveRole(
            adminRole,
            keccak256(
                abi.encodePacked(
                    AUTHORIZATION_EXPIRATION_EXTENDER_ROLE_DESCRIPTION
                )
            )
        );
        authorizationExpirationSetterRole = _deriveRole(
            adminRole,
            keccak256(
                abi.encodePacked(
                    AUTHORIZATION_EXPIRATION_SETTER_ROLE_DESCRIPTION
                )
            )
        );
        indefiniteAuthorizerRole = _deriveRole(
            adminRole,
            keccak256(abi.encodePacked(INDEFINITE_AUTHORIZER_ROLE_DESCRIPTION))
        );
    }

    /// @notice Extends the expiration of the temporary authoriztion of the
    /// requester for  the Airnode if the sender is allowed to extend
    /// authorization expiration
    /// @param airnode Airnode address
    /// @param requester Requester address
    /// @param expirationTimestamp Timestamp at which the temporary
    /// authorization will expire
    function extendAuthorizerExpiration(
        address airnode,
        address requester,
        uint32 expirationTimestamp
    ) external override {
        require(
            hasAuthorizationExpirationExtenderRoleOrIsManager(_msgSender()),
            "Cannot extend expiration"
        );
        _extendAuthorizationExpiration(airnode, requester, expirationTimestamp);
    }

    /// @notice Sets the expiration of the temporary authorization of the
    /// requester for the Airnode if the sender is allowed to set expiration
    /// @dev Unlike `extendAuthorizerExpiration()`, this can hasten expiration
    /// @param airnode Airnode address
    /// @param requester Requester address
    /// @param expirationTimestamp Timestamp at which the temporary
    /// authorization will expire
    function setAuthorizationExpiration(
        address airnode,
        address requester,
        uint32 expirationTimestamp
    ) external override {
        require(
            hasAuthorizationExpirationSetterRoleOrIsManager(_msgSender()),
            "Cannot set expiration"
        );
        _setAuthorizationExpiration(airnode, requester, expirationTimestamp);
    }

    /// @notice Sets the indefinite authorizer status of the requester for the
    /// Airnode if the sender is allowed to authorize indefinitely
    /// @param airnode Airnode address
    /// @param requester Requester address
    /// @param status Indefinite authorizer status
    function setIndefiniteAuthorizationStatus(
        address airnode,
        address requester,
        bool status
    ) external override {
        require(
            hasIndefiniteAuthorizerRoleOrIsManager(_msgSender()),
            "Cannot set indefinite status"
        );
        _setIndefiniteAuthorizationStatus(airnode, requester, status);
    }

    /// @notice Revokes the indefinite authorization status granted by a
    /// specific account that no longer has the indefinite authorizer role
    /// @param airnode Airnode address
    /// @param requester Requester address
    /// @param setter Setter of the indefinite authorization status
    function revokeIndefiniteAuthorizationStatus(
        address airnode,
        address requester,
        address setter
    ) external override {
        require(
            !hasIndefiniteAuthorizerRoleOrIsManager(setter),
            "setter can set indefinite status"
        );
        _revokeIndefiniteAuthorizationStatus(airnode, requester, setter);
    }

    /// @dev Returns if the account has the authorization expiration extender
    /// role or is the manager
    /// @param account Account address
    /// @return If the account has the authorization extender role or is the
    /// manager
    function hasAuthorizationExpirationExtenderRoleOrIsManager(
        address account
    ) private view returns (bool) {
        return
            manager == account ||
            IAccessControlRegistry(accessControlRegistry).hasRole(
                authorizationExpirationExtenderRole,
                account
            );
    }

    /// @dev Returns if the account has the authorization expiration setter
    /// role or is the manager
    /// @param account Account address
    /// @return If the account has the authorization expiration setter role or
    /// is the manager
    function hasAuthorizationExpirationSetterRoleOrIsManager(
        address account
    ) private view returns (bool) {
        return
            manager == account ||
            IAccessControlRegistry(accessControlRegistry).hasRole(
                authorizationExpirationSetterRole,
                account
            );
    }

    /// @dev Returns if the account has the indefinite authorizer role or is
    /// the manager
    /// @param account Account address
    /// @return If the account has the indefinite authorizer role or is the
    /// manager
    function hasIndefiniteAuthorizerRoleOrIsManager(
        address account
    ) private view returns (bool) {
        return
            manager == account ||
            IAccessControlRegistry(accessControlRegistry).hasRole(
                indefiniteAuthorizerRole,
                account
            );
    }

    /// @dev See Context.sol
    function _msgSender()
        internal
        view
        virtual
        override(RequesterAuthorizer, ERC2771Context)
        returns (address)
    {
        return ERC2771Context._msgSender();
    }
}

contracts/protocol/AirnodeProtocol.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.17;

import "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";
import "../utils/ExtendedSelfMulticall.sol";
import "./StorageUtils.sol";
import "./SponsorshipUtils.sol";
import "./WithdrawalUtils.sol";
import "./interfaces/IAirnodeProtocol.sol";

/// @title Airnode requestresponse protocol (RRP) and its relayed version
/// @notice Similar to HTTP, RRP allows the requester to specify a one-off
/// request that the Airnode is expected to respond to as soon as possible.
/// The relayed version allows the requester to specify an Airnode that will
/// sign the fulfillment data and a relayer that will report the signed
/// fulfillment.
/// @dev StorageUtils, SponsorshipUtils and WithdrawalUtils also implement some
/// auxiliary functionality for PSP
contract AirnodeProtocol is
    ExtendedSelfMulticall,
    StorageUtils,
    SponsorshipUtils,
    WithdrawalUtils,
    IAirnodeProtocol
{
    using ECDSA for bytes32;

    /// @notice Number of requests the requester has made
    mapping(address => uint256) public override requesterToRequestCount;

    mapping(bytes32 => bytes32) private requestIdToFulfillmentParameters;

    /// @notice Called by the requester to make a request
    /// @dev It is the responsibility of the respective Airnode to resolve if
    /// `endpointOrTemplateId` is an endpoint ID or a template ID
    /// @param endpointOrTemplateId Endpoint or template ID
    /// @param parameters Parameters
    /// @param sponsor Sponsor address
    /// @param fulfillFunctionId Selector of the function to be called for
    /// fulfillment
    /// @return requestId Request ID
    function makeRequest(
        address airnode,
        bytes32 endpointOrTemplateId,
        bytes calldata parameters,
        address sponsor,
        bytes4 fulfillFunctionId
    ) external override returns (bytes32 requestId) {
        require(airnode != address(0), "Airnode address zero");
        require(
            endpointOrTemplateId != bytes32(0),
            "Endpoint or template ID zero"
        );
        require(
            parameters.length <= MAXIMUM_PARAMETER_LENGTH,
            "Parameters too long"
        );
        require(sponsor != address(0), "Sponsor address zero");
        require(fulfillFunctionId != bytes4(0), "Fulfill function ID zero");
        uint256 requesterRequestCount = ++requesterToRequestCount[msg.sender];
        requestId = keccak256(
            abi.encodePacked(
                block.chainid,
                address(this),
                msg.sender,
                requesterRequestCount,
                airnode,
                endpointOrTemplateId,
                parameters,
                sponsor,
                fulfillFunctionId
            )
        );
        requestIdToFulfillmentParameters[requestId] = keccak256(
            abi.encodePacked(airnode, msg.sender, fulfillFunctionId)
        );
        emit MadeRequest(
            airnode,
            requestId,
            msg.sender,
            requesterRequestCount,
            endpointOrTemplateId,
            parameters,
            sponsor,
            fulfillFunctionId
        );
    }

    /// @notice Called by the Airnode using the sponsor wallet to fulfill the
    /// request
    /// @dev Airnodes attest to controlling their respective sponsor wallets by
    /// signing a message with the address of the sponsor wallet. A timestamp
    /// is added to this signature for it to act as an expiring token if the
    /// requester contract checks for freshness.
    /// This will not revert depending on the external call. However, it will
    /// return `false` if the external call reverts or if there is no function
    /// with a matching signature at `fulfillAddress`. On the other hand, it
    /// will return `true` if the external call returns successfully or if
    /// there is no contract deployed at `fulfillAddress`.
    /// If `callSuccess` is `false`, `callData` can be decoded to retrieve the
    /// revert string.
    /// @param requestId Request ID
    /// @param airnode Airnode address
    /// @param requester Requester address
    /// @param fulfillFunctionId Selector of the function to be called for
    /// fulfillment
    /// @param timestamp Timestamp used in the signature
    /// @param data Fulfillment data, encoded in contract ABI
    /// @param signature Request ID, a timestamp and the sponsor wallet address
    /// signed by the Airnode wallet
    /// @return callSuccess If the fulfillment call succeeded
    /// @return callData Data returned by the fulfillment call (if there is
    /// any)
    function fulfillRequest(
        bytes32 requestId,
        address airnode,
        address requester,
        bytes4 fulfillFunctionId,
        uint256 timestamp,
        bytes calldata data,
        bytes calldata signature
    ) external override returns (bool callSuccess, bytes memory callData) {
        require(
            keccak256(
                abi.encodePacked(airnode, requester, fulfillFunctionId)
            ) == requestIdToFulfillmentParameters[requestId],
            "Invalid request fulfillment"
        );
        require(
            (
                keccak256(abi.encodePacked(requestId, timestamp, msg.sender))
                    .toEthSignedMessageHash()
            ).recover(signature) == airnode,
            "Signature mismatch"
        );
        delete requestIdToFulfillmentParameters[requestId];
        // solhint-disable-next-line avoid-low-level-calls
        (callSuccess, callData) = requester.call(
            abi.encodeWithSelector(
                fulfillFunctionId,
                requestId,
                timestamp,
                data
            )
        );
        if (callSuccess) {
            emit FulfilledRequest(airnode, requestId, timestamp, data);
        } else {
            // We do not bubble up the revert string from `callData`
            emit FailedRequest(
                airnode,
                requestId,
                timestamp,
                "Fulfillment failed unexpectedly"
            );
        }
    }

    /// @notice Called by the Airnode using the sponsor wallet if the request
    /// cannot be fulfilled
    /// @dev The Airnode should fall back to this if a request cannot be
    /// fulfilled because of an error, including the static call to `fulfill()`
    /// returning `false` for `callSuccess`
    /// @param requestId Request ID
    /// @param airnode Airnode address
    /// @param requester Requester address
    /// @param fulfillFunctionId Selector of the function to be called for
    /// fulfillment
    /// @param timestamp Timestamp used in the signature
    /// @param errorMessage A message that explains why the request has failed
    /// @param signature Request ID, a timestamp and the sponsor wallet address
    /// signed by the Airnode address
    function failRequest(
        bytes32 requestId,
        address airnode,
        address requester,
        bytes4 fulfillFunctionId,
        uint256 timestamp,
        string calldata errorMessage,
        bytes calldata signature
    ) external override {
        require(
            keccak256(
                abi.encodePacked(airnode, requester, fulfillFunctionId)
            ) == requestIdToFulfillmentParameters[requestId],
            "Invalid request fulfillment"
        );
        require(
            (
                keccak256(abi.encodePacked(requestId, timestamp, msg.sender))
                    .toEthSignedMessageHash()
            ).recover(signature) == airnode,
            "Signature mismatch"
        );
        delete requestIdToFulfillmentParameters[requestId];
        emit FailedRequest(airnode, requestId, timestamp, errorMessage);
    }

    /// @notice Called by the requester to make a request to be fulfilled by a
    /// relayer
    /// @dev The relayer address is indexed in the relayed protocol logs
    /// because it will be the relayer that will be listening to these logs
    /// @param endpointOrTemplateId Endpoint or template ID
    /// @param parameters Parameters
    /// @param relayer Relayer address
    /// @param sponsor Sponsor address
    /// @param fulfillFunctionId Selector of the function to be called for
    /// fulfillment
    /// @return requestId Request ID
    function makeRequestRelayed(
        address airnode,
        bytes32 endpointOrTemplateId,
        bytes calldata parameters,
        address relayer,
        address sponsor,
        bytes4 fulfillFunctionId
    ) external override returns (bytes32 requestId) {
        require(airnode != address(0), "Airnode address zero");
        require(
            endpointOrTemplateId != bytes32(0),
            "Endpoint or template ID zero"
        );
        require(
            parameters.length <= MAXIMUM_PARAMETER_LENGTH,
            "Parameters too long"
        );
        require(relayer != address(0), "Relayer address zero");
        require(sponsor != address(0), "Sponsor address zero");
        require(fulfillFunctionId != bytes4(0), "Fulfill function ID zero");
        uint256 requesterRequestCount = ++requesterToRequestCount[msg.sender];
        requestId = keccak256(
            abi.encodePacked(
                block.chainid,
                address(this),
                msg.sender,
                requesterRequestCount,
                airnode,
                endpointOrTemplateId,
                parameters,
                relayer,
                sponsor,
                fulfillFunctionId
            )
        );
        requestIdToFulfillmentParameters[requestId] = keccak256(
            abi.encodePacked(airnode, msg.sender, relayer, fulfillFunctionId)
        );
        emit MadeRequestRelayed(
            relayer,
            requestId,
            airnode,
            msg.sender,
            requesterRequestCount,
            endpointOrTemplateId,
            parameters,
            sponsor,
            fulfillFunctionId
        );
    }

    /// @notice Called by the relayer using the sponsor wallet to fulfill the
    /// request with the Airnode-signed response
    /// @dev The Airnode must verify the integrity of the request details,
    /// template details, sponsor addresssponsor wallet address consistency
    /// before signing the response
    /// @param requestId Request ID
    /// @param airnode Airnode address
    /// @param requester Requester address
    /// @param relayer Relayer address
    /// @param fulfillFunctionId Selector of the function to be called for
    /// fulfillment
    /// @param timestamp Timestamp used in the signature
    /// @param data Fulfillment data
    /// @param signature Request ID, a timestamp, the sponsor wallet address
    /// and the fulfillment data signed by the Airnode address
    /// @return callSuccess If the fulfillment call succeeded
    /// @return callData Data returned by the fulfillment call (if there is
    /// any)
    function fulfillRequestRelayed(
        bytes32 requestId,
        address airnode,
        address requester,
        address relayer,
        bytes4 fulfillFunctionId,
        uint256 timestamp,
        bytes calldata data,
        bytes calldata signature
    ) external override returns (bool callSuccess, bytes memory callData) {
        require(
            keccak256(
                abi.encodePacked(airnode, requester, relayer, fulfillFunctionId)
            ) == requestIdToFulfillmentParameters[requestId],
            "Invalid request fulfillment"
        );
        require(
            (
                keccak256(
                    abi.encodePacked(requestId, timestamp, msg.sender, data)
                ).toEthSignedMessageHash()
            ).recover(signature) == airnode,
            "Signature mismatch"
        );
        delete requestIdToFulfillmentParameters[requestId];
        // solhint-disable-next-line avoid-low-level-calls
        (callSuccess, callData) = requester.call(
            abi.encodeWithSelector(
                fulfillFunctionId,
                requestId,
                timestamp,
                data
            )
        );
        if (callSuccess) {
            emit FulfilledRequestRelayed(
                relayer,
                requestId,
                airnode,
                timestamp,
                data
            );
        } else {
            // We do not bubble up the revert string from `callData`
            emit FailedRequestRelayed(
                relayer,
                requestId,
                airnode,
                timestamp,
                "Fulfillment failed unexpectedly"
            );
        }
    }

    /// @notice Called by the relayer using the sponsor wallet if the request
    /// cannot be fulfilled
    /// @dev Since failure may also include problems at the Airnode-end (such
    /// as it being unavailable), we are content with a signature from the
    /// relayer to validate failures. This is acceptable because explicit
    /// failures are mainly for easy debugging of issues, and the requester
    /// should always consider denial of service from a relayer or an Airnode
    /// to be a possibility.
    /// @param requestId Request ID
    /// @param airnode Airnode address
    /// @param requester Requester address
    /// @param relayer Relayer address
    /// @param timestamp Timestamp used in the signature
    /// @param errorMessage A message that explains why the request has failed
    /// @param signature Request ID, a timestamp and the sponsor wallet address
    /// signed by the relayer address
    function failRequestRelayed(
        bytes32 requestId,
        address airnode,
        address requester,
        address relayer,
        bytes4 fulfillFunctionId,
        uint256 timestamp,
        string calldata errorMessage,
        bytes calldata signature
    ) external override {
        require(
            keccak256(
                abi.encodePacked(airnode, requester, relayer, fulfillFunctionId)
            ) == requestIdToFulfillmentParameters[requestId],
            "Invalid request fulfillment"
        );
        require(
            (
                keccak256(abi.encodePacked(requestId, timestamp, msg.sender))
                    .toEthSignedMessageHash()
            ).recover(signature) == relayer,
            "Signature mismatch"
        );
        delete requestIdToFulfillmentParameters[requestId];
        emit FailedRequestRelayed(
            relayer,
            requestId,
            airnode,
            timestamp,
            errorMessage
        );
    }

    /// @notice Returns if the request with the ID is made but not
    /// fulfilled/failed yet
    /// @dev If a requester has made a request, received a request ID but did
    /// not hear back, it can call this method to check if the Airnode/relayer
    /// called back `failRequest()`/`failRequestRelayed()` instead.
    /// @param requestId Request ID
    /// @return If the request is awaiting fulfillment
    function requestIsAwaitingFulfillment(
        bytes32 requestId
    ) external view override returns (bool) {
        return requestIdToFulfillmentParameters[requestId] != bytes32(0);
    }
}

contracts/protocol/AirnodeRequester.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "./interfaces/IAirnodeProtocol.sol";
import "./interfaces/IAirnodeRequester.sol";

/// @title Contract to be inherited by contracts that will make Airnode
/// requests and receive fulfillments
abstract contract AirnodeRequester is IAirnodeRequester {
    /// @notice AirnodeProtocol contract address
    address public immutable override airnodeProtocol;

    /// @dev Reverts if the sender is not the AirnodeProtocol contract. Use
    /// this modifier with methods that are meant to receive RRP fulfillments.
    modifier onlyAirnodeProtocol() {
        require(
            msg.sender == address(airnodeProtocol),
            "Sender not Airnode protocol"
        );
        _;
    }

    /// @dev Reverts if the timestamp is not valid. Use this modifier with
    /// methods that are meant to receive RRP and PSP fulfillments.
    /// @param timestamp Timestamp used in the signature
    modifier onlyValidTimestamp(uint256 timestamp) {
        require(timestampIsValid(timestamp), "Timestamp not valid");
        _;
    }

    /// @param _airnodeProtocol AirnodeProtocol contract address
    constructor(address _airnodeProtocol) {
        require(_airnodeProtocol != address(0), "AirnodeProtocol address zero");
        airnodeProtocol = _airnodeProtocol;
    }

    /// @notice Overriden by the inheriting contract to return if the timestamp
    /// used in the signature is valid
    /// @dev If and how the timestamp should be validated depends on the nature
    /// of the request. If the request is "return me the price of this asset at
    /// this specific time in history", it can be assumed that the response
    /// will not go out of date. If the request is "return me the price of this
    /// asset now", the requester would rather not consider a response that is
    /// not immediate.
    /// In addition to the nature of the request, if and how the timestamp is
    /// used in the contract logic determines how it should be validated.
    /// In general, one should keep in mind that similar to the fulfillment
    /// data, it is possible for the timestamp to be misreported.
    /// @param timestamp Timestamp used in the signature
    function timestampIsValid(
        uint256 timestamp
    ) internal view virtual returns (bool);
}

