contracts/protocol/SponsorshipUtils.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.17;

import "./interfaces/ISponsorshipUtils.sol";

/// @title Contract that sponsors can use to announce their willingness to
/// sponsor a particular RRP requester or PSP subscription
/// @notice The sponsorship status is not checked during requests or
/// fulfillments, which means the respective Airnode is trusted to make this
/// check through a static call to this contract. The Airnode may skip this
/// check if it has received an off-chain assurance.
/// @dev An Airnode (or relayer) has a "sponsor wallet" dedicated for each
/// account through an HD wallet. When a requester makes a request specifying a
/// sponsor, the Airnode verifies the sponsorship my making a static call to
/// this contract, and uses the respective sponsor wallet to fulfill the
/// request. This allows the sponsor to cover the gas costs of the
/// fulfillments, as they know that funds they have deposited in the respective
/// sponsor wallet will only be used for use-cases they have sponsored.
contract SponsorshipUtils is ISponsorshipUtils {
    /// @notice Sponsorship status for a sponsor–RRP requester pair
    mapping(address => mapping(address => bool))
        public
        override sponsorToRequesterToRrpSponsorshipStatus;

    /// @notice Sponsorship status for a sponsor–PSP subscription pair
    mapping(address => mapping(bytes32 => bool))
        public
        override sponsorToSubscriptionIdToPspSponsorshipStatus;

    /// @notice Called by the sponsor to set the sponsorship status of an RRP
    /// requester
    /// @dev This applies to both regular and relayed RRP requests
    /// @param requester RRP requester address
    /// @param status Sponsorship status
    function setRrpSponsorshipStatus(
        address requester,
        bool status
    ) external override {
        require(requester != address(0), "Requester address zero");
        sponsorToRequesterToRrpSponsorshipStatus[msg.sender][
            requester
        ] = status;
        emit SetRrpSponsorshipStatus(msg.sender, requester, status);
    }

    /// @notice Called by the sponsor to set the sponsorship status of a PSP
    /// subscription
    /// @param subscriptionId Subscription ID
    /// @param status Sponsorship status
    function setPspSponsorshipStatus(
        bytes32 subscriptionId,
        bool status
    ) external override {
        require(subscriptionId != bytes32(0), "Subscription ID zero");
        sponsorToSubscriptionIdToPspSponsorshipStatus[msg.sender][
            subscriptionId
        ] = status;
        emit SetPspSponsorshipStatus(msg.sender, subscriptionId, status);
    }
}

contracts/protocol/StorageUtils.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.17;

import "./interfaces/IStorageUtils.sol";

/// @title Contract that stores template and subscription details on chain
/// @notice The Airnode protocol does not depend on the template or
/// subscription details being stored on-chain. Airnode can be informed about
/// these in other ways, e.g., the details are hardcoded in the Airnode
/// configuration file.
contract StorageUtils is IStorageUtils {
    struct Template {
        bytes32 endpointId;
        bytes parameters;
    }

    struct Subscription {
        uint256 chainId;
        address airnode;
        bytes32 endpointOrTemplateId;
        bytes parameters;
        bytes conditions;
        address relayer;
        address sponsor;
        address requester;
        bytes4 fulfillFunctionId;
    }

    /// @notice Maximum parameter length for byte strings that Airnodes will
    /// need to read from storage or logs
    /// @dev A very generous limit is applied, under the assumption that
    /// anything larger than this is a grief attempt. If the user needs to use
    /// longer parameters, they will need to use off-chain channels to pass
    /// the respective template/subscription details to the Airnode operator
    /// for them to be specified in the configuration file.
    uint256 public constant override MAXIMUM_PARAMETER_LENGTH = 4096;

    /// @notice Template details with the ID
    mapping(bytes32 => Template) public override templates;

    /// @notice Subscription details with the ID
    mapping(bytes32 => Subscription) public override subscriptions;

    /// @notice Stores template details
    /// @dev Templates fully or partially define requests. By referencing a
    /// template, requesters can omit specifying the "boilerplate" sections of
    /// requests.
    /// In a subscription context, a zero endpoint ID means the Airnode does
    /// not need to use one of its endpoints, and can move directly on to
    /// fulfillment. This is particularly useful for defining traditional
    /// keeper jobs that do not require off-chain data.
    /// @param endpointId Endpoint ID (allowed to be `bytes32(0)`)
    /// @param parameters Template parameters, encoded in Airnode ABI
    /// @return templateId Template ID
    function storeTemplate(
        bytes32 endpointId,
        bytes calldata parameters
    ) external override returns (bytes32 templateId) {
        require(
            parameters.length <= MAXIMUM_PARAMETER_LENGTH,
            "Parameters too long"
        );
        templateId = keccak256(abi.encodePacked(endpointId, parameters));
        templates[templateId] = Template({
            endpointId: endpointId,
            parameters: parameters
        });
        emit StoredTemplate(templateId, endpointId, parameters);
    }

    /// @notice Stores subscription details
    /// @dev `airnode` should make the query specified by `templateId` and
    /// `parameters`. If the returned data satisfies `conditions`, it should
    /// call `requester`'s `fulfillFunctionId` on `chainId` with the returned
    /// data, using the wallet dedicated to `sponsor`.
    /// If `relayer` is not `airnode`, the relayer is responsible with checking
    /// `condition` and using the wallet dedicated to `sponsor` to deliver the
    /// data.
    /// In most cases, `conditions` will specify a static call to a function on
    /// `chainId` with the data. The extent of its flexibility depends on the
    /// node implementation and is outside the scope of the on-chain protocol.
    /// Similarly, `conditions` can specify with what frequency it should be
    /// verified, and the details of this is outside the scope.
    /// `templateId` being zero is similar to the endpoint ID being zero for
    /// templates, means the endpoint query can be skipped. In this case,
    /// `parameters` will be treated as the data that is returned by the
    /// endpoint while verifying `conditions`.
    /// @param chainId Chain ID
    /// @param airnode Airnode address
    /// @param endpointOrTemplateId Endpoint or template ID (allowed to be
    /// `bytes32(0)`)
    /// @param parameters Parameters provided by the subscription in addition
    /// to the parameters in the template (if applicable), encoded in Airnode
    /// ABI
    /// @param conditions Conditions under which the subscription is requested
    /// to be fulfilled, encoded in Airnode ABI
    /// @param relayer Relayer address
    /// @param sponsor Sponsor address
    /// @param requester Requester address
    /// @param fulfillFunctionId Selector of the function to be called for
    /// fulfillment
    /// @return subscriptionId Subscription ID
    function storeSubscription(
        uint256 chainId,
        address airnode,
        bytes32 endpointOrTemplateId,
        bytes calldata parameters,
        bytes calldata conditions,
        address relayer,
        address sponsor,
        address requester,
        bytes4 fulfillFunctionId
    ) external override returns (bytes32 subscriptionId) {
        require(chainId != 0, "Chain ID zero");
        require(airnode != address(0), "Airnode address zero");
        require(
            parameters.length <= MAXIMUM_PARAMETER_LENGTH,
            "Parameters too long"
        );
        require(
            conditions.length <= MAXIMUM_PARAMETER_LENGTH,
            "Conditions too long"
        );
        require(relayer != address(0), "Relayer address zero");
        require(sponsor != address(0), "Sponsor address zero");
        require(requester != address(0), "Requester address zero");
        require(fulfillFunctionId != bytes4(0), "Fulfill function ID zero");
        subscriptionId = keccak256(
            abi.encode(
                chainId,
                airnode,
                endpointOrTemplateId,
                parameters,
                conditions,
                relayer,
                sponsor,
                requester,
                fulfillFunctionId
            )
        );
        subscriptions[subscriptionId] = Subscription({
            chainId: chainId,
            airnode: airnode,
            endpointOrTemplateId: endpointOrTemplateId,
            parameters: parameters,
            conditions: conditions,
            relayer: relayer,
            sponsor: sponsor,
            requester: requester,
            fulfillFunctionId: fulfillFunctionId
        });
        emit StoredSubscription(
            subscriptionId,
            chainId,
            airnode,
            endpointOrTemplateId,
            parameters,
            conditions,
            relayer,
            sponsor,
            requester,
            fulfillFunctionId
        );
    }
}

contracts/protocol/WithdrawalUtils.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.17;

import "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";
import "./interfaces/IWithdrawalUtils.sol";

/// @title Contract that can be used by sponsors to request withdrawals from
/// sponsor wallets and Airnodes/relayers to fulfill these
/// @notice The respective Airnode/relayer may not support withdrawals for the
/// specified protocol, or at all. Similarly, an Airnode/relayer may deposit
/// funds directly to the sponsor address without being prompted, e.g., because
/// they are ceasing operations. In general, no guarantee is provided for the
/// funds deposited to sponsor wallets at the protocol level. Therefore, the
/// sponsors should limit their deposits to the minimum amount required for
/// their operations, and assume they will not receive these funds back.
/// @dev Withdrawals are implemented in the form of pull payments. The sponsor
/// requests a withdrawal from a sponsor wallet, and the Airnode/relayer uses
/// the specified sponsor wallet to deposit the entire balance at this
/// contract. Then, the sponsor claims/pulls the payment from this contract.
/// Different protocols (RRP, PSP, etc.) use different sponsor wallets for a
/// particular Airnode/relayer–sponsor pair, which is why sponsor wallet
/// derivation includes a protocol ID. Refer to the node documentation for what
/// these protocol IDs are.
contract WithdrawalUtils is IWithdrawalUtils {
    using ECDSA for bytes32;

    /// @notice Sponsor balance that is withdrawn but not claimed
    mapping(address => uint256) public override sponsorToBalance;

    /// @notice Number of withdrawal requests the sponsor made
    mapping(address => uint256) public override sponsorToWithdrawalRequestCount;

    mapping(bytes32 => bytes32) private withdrawalRequestIdToParameters;

    /// @notice Called by a sponsor to request a withdrawal. In response, the
    /// Airnode/relayer is expected to deposit the funds at this contract by
    /// calling `fulfillWithdrawal()`, and then the sponsor will have to call
    /// `claimBalance()` to have the funds sent to itself. For sponsor to be
    /// able to receive funds this way, it has to be an EOA or a contract that
    /// has an appropriate payable fallback function.
    /// @param airnodeOrRelayer Airnode/relayer address
    /// @param protocolId Protocol ID
    function requestWithdrawal(
        address airnodeOrRelayer,
        uint256 protocolId
    ) external override {
        require(airnodeOrRelayer != address(0), "Airnode/relayer address zero");
        require(protocolId != 0, "Protocol ID zero");
        bytes32 withdrawalRequestId = keccak256(
            abi.encodePacked(
                block.chainid,
                address(this),
                msg.sender,
                ++sponsorToWithdrawalRequestCount[msg.sender]
            )
        );
        withdrawalRequestIdToParameters[withdrawalRequestId] = keccak256(
            abi.encodePacked(airnodeOrRelayer, protocolId, msg.sender)
        );
        emit RequestedWithdrawal(
            airnodeOrRelayer,
            msg.sender,
            withdrawalRequestId,
            protocolId
        );
    }

    /// @notice Called by the Airnode/relayer using the sponsor wallet to
    /// fulfill the withdrawal request made by the sponsor
    /// @param withdrawalRequestId Withdrawal request ID
    /// @param airnodeOrRelayer Airnode/relayer address
    /// @param protocolId Protocol ID
    /// @param sponsor Sponsor address
    function fulfillWithdrawal(
        bytes32 withdrawalRequestId,
        address airnodeOrRelayer,
        uint256 protocolId,
        address sponsor,
        uint256 timestamp,
        bytes calldata signature
    ) external payable override {
        require(
            withdrawalRequestIdToParameters[withdrawalRequestId] ==
                keccak256(
                    abi.encodePacked(airnodeOrRelayer, protocolId, sponsor)
                ),
            "Invalid withdrawal fulfillment"
        );
        unchecked {
            require(
                timestamp + 1 hours > block.timestamp &&
                    timestamp < block.timestamp + 1 hours,
                "Timestamp not valid"
            );
        }
        require(
            (
                keccak256(
                    abi.encodePacked(withdrawalRequestId, timestamp, msg.sender)
                ).toEthSignedMessageHash()
            ).recover(signature) == airnodeOrRelayer,
            "Signature mismatch"
        );
        delete withdrawalRequestIdToParameters[withdrawalRequestId];
        sponsorToBalance[sponsor] += msg.value;
        emit FulfilledWithdrawal(
            airnodeOrRelayer,
            sponsor,
            withdrawalRequestId,
            protocolId,
            msg.sender,
            msg.value
        );
    }

    /// @notice Called by the sponsor to claim the withdrawn funds
    /// @dev The sponsor must be able to receive funds. For example, if the
    /// sponsor is a contract without a default `payable` function, this will
    /// revert.
    function claimBalance() external override {
        uint256 sponsorBalance = sponsorToBalance[msg.sender];
        require(sponsorBalance != 0, "Sender balance zero");
        sponsorToBalance[msg.sender] = 0;
        emit ClaimedBalance(msg.sender, sponsorBalance);
        // solhint-disable-next-line avoid-low-level-calls
        (bool success, ) = msg.sender.call{value: sponsorBalance}("");
        require(success, "Transfer failed");
    }

    /// @notice Returns if the withdrawal request with the ID is made but not
    /// fulfilled yet
    /// @param withdrawalRequestId Withdrawal request ID
    /// @return isAwaitingFulfillment If the withdrawal request is awaiting
    /// fulfillment
    function withdrawalRequestIsAwaitingFulfillment(
        bytes32 withdrawalRequestId
    ) external view override returns (bool) {
        return
            withdrawalRequestIdToParameters[withdrawalRequestId] != bytes32(0);
    }
}

contracts/utils/ExpiringMetaTxForwarder..content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/utils/Context.sol";
import "@openzeppelin/contracts/utils/cryptography/EIP712.sol";
import "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";
import "@openzeppelin/contracts/utils/Address.sol";
import "./interfaces/IExpiringMetaTxForwarder.sol";

/// @title Contract that forwards expiring meta-txes to ERC2771 contracts that
/// trust it
/// @notice `msg.value` is not supported. Identical meta-txes are not
/// supported. Target account must be a contract. Signer of the meta-tx is
/// allowed to cancel it before execution, effectively rendering the signature
/// useless.
/// This implementation is not intended to be used with general-purpose relayer
/// networks. Instead, it is meant for use-cases where the relayer wants the
/// signer to send a tx, so they request a meta-tx and execute it themselves to
/// cover the gas cost.
/// @dev This implementation does not use signer-specific nonces for meta-txes
/// to be executable in an arbitrary order. For example, one can sign two
/// meta-txes that will whitelist an account each and deliver these to the
/// respective owners of the accounts. This implementation allows the account
/// owners to not care about if and when the other meta-tx is executed. The
/// signer is responsible for not issuing signatures that may cause undesired
/// race conditions.
contract ExpiringMetaTxForwarder is Context, EIP712, IExpiringMetaTxForwarder {
    using ECDSA for bytes32;
    using Address for address;

    /// @notice If the meta-tx with hash is executed or canceled
    /// @dev We track this on a meta-tx basis and not by using nonces to avoid
    /// requiring users keep track of nonces
    mapping(bytes32 => bool) public override metaTxWithHashIsExecutedOrCanceled;

    bytes32 private constant _TYPEHASH =
        keccak256(
            "ExpiringMetaTx(address from,address to,bytes data,uint256 expirationTimestamp)"
        );

    constructor() EIP712("ExpiringMetaTxForwarder", "1.0.0") {}

    /// @notice Verifies the signature and executes the meta-tx
    /// @param metaTx Meta-tx
    /// @param signature Meta-tx hash signed by `from`
    /// @return returndata Returndata
    function execute(
        ExpiringMetaTx calldata metaTx,
        bytes calldata signature
    ) external override returns (bytes memory returndata) {
        bytes32 metaTxHash = processMetaTx(metaTx);
        require(
            metaTxHash.recover(signature) == metaTx.from,
            "Invalid signature"
        );
        emit ExecutedMetaTx(metaTxHash);
        returndata = metaTx.to.functionCall(
            abi.encodePacked(metaTx.data, metaTx.from)
        );
    }

    /// @notice Called by a meta-tx source to prevent it from being executed
    /// @dev This can be used to cancel meta-txes that were issued
    /// accidentally, e.g., with an unreasonably large expiration timestamp,
    /// which may create a dangling liability
    /// @param metaTx Meta-tx
    function cancel(ExpiringMetaTx calldata metaTx) external override {
        require(_msgSender() == metaTx.from, "Sender not meta-tx source");
        emit CanceledMetaTx(processMetaTx(metaTx));
    }

    /// @notice Checks if the meta-tx is valid, invalidates it for future
    /// execution or nullification, and returns the meta-tx hash
    /// @param metaTx Meta-tx
    /// @return metaTxHash Meta-tx hash
    function processMetaTx(
        ExpiringMetaTx calldata metaTx
    ) private returns (bytes32 metaTxHash) {
        metaTxHash = _hashTypedDataV4(
            keccak256(
                abi.encode(
                    _TYPEHASH,
                    metaTx.from,
                    metaTx.to,
                    keccak256(metaTx.data),
                    metaTx.expirationTimestamp
                )
            )
        );
        require(
            !metaTxWithHashIsExecutedOrCanceled[metaTxHash],
            "Meta-tx executed or canceled"
        );
        require(
            metaTx.expirationTimestamp > block.timestamp,
            "Meta-tx expired"
        );
        metaTxWithHashIsExecutedOrCanceled[metaTxHash] = true;
    }
}

contracts/utils/ExtendedSelfMulticall.so.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.7;

import "./SelfMulticall.sol";
import "./interfaces/IExtendedSelfMulticall.sol";

/// @title Contract that extends SelfMulticall to fetch some of the global
/// variables
/// @notice Available global variables are limited to the ones that Airnode
/// tends to need
contract ExtendedSelfMulticall is SelfMulticall, IExtendedSelfMulticall {
    /// @notice Returns the chain ID
    /// @return Chain ID
    function getChainId() external view override returns (uint256) {
        return block.chainid;
    }

    /// @notice Returns the account balance
    /// @param account Account address
    /// @return Account balance
    function getBalance(
        address account
    ) external view override returns (uint256) {
        return account.balance;
    }

    /// @notice Returns if the account contains bytecode
    /// @dev An account not containing any bytecode does not indicate that it
    /// is an EOA or it will not contain any bytecode in the future.
    /// Contract construction and `SELFDESTRUCT` updates the bytecode at the
    /// end of the transaction.
    /// @return If the account contains bytecode
    function containsBytecode(
        address account
    ) external view override returns (bool) {
        return account.code.length > 0;
    }

    /// @notice Returns the current block number
    /// @return Current block number
    function getBlockNumber() external view override returns (uint256) {
        return block.number;
    }

    /// @notice Returns the current block timestamp
    /// @return Current block timestamp
    function getBlockTimestamp() external view override returns (uint256) {
        return block.timestamp;
    }

    /// @notice Returns the current block basefee
    /// @return Current block basefee
    function getBlockBasefee() external view override returns (uint256) {
        return block.basefee;
    }
}

contracts/utils/ExternalMulticall.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "./interfaces/IExternalMulticall.sol";

/// @title Contract that enables calls to external contracts to be batched
/// @notice This contract can be used for two use-cases: (1) In its current
/// state, it can be used to batch static calls to contracts that do not care
/// about who the sender is, (2) after extending it, to interact with trusted
/// contracts (see below for details). It implements two ways of batching, one
/// requires none of the calls to revert and the other tolerates individual
/// calls reverting.
/// @dev As mentioned above, this contract can be used to interact with trusted
/// contracts. Such interactions can leave this contract in a privileged
/// position (e.g., ExternalMulticall may be left with a non-zero balance of an
/// ERC20 token as a result of a transaction sent to it), which can be abused
/// by an attacker afterwards. In addition, attackers can frontrun interactions
/// to have the following interaction result in an unintended outcome. A
/// general solution to these attacks is overriding both multicall functions
/// behind an access control mechanism, such as an `onlyOwner` modifier.
/// Refer to MakerDAO's Multicall.sol for a similar implementation.
abstract contract ExternalMulticall is IExternalMulticall {
    /// @notice Batches calls to external contracts and reverts as soon as one
    /// of the batched calls reverts
    /// @param targets Array of target addresses of batched calls
    /// @param data Array of calldata of batched calls
    /// @return returndata Array of returndata of batched calls
    function externalMulticall(
        address[] calldata targets,
        bytes[] calldata data
    ) public virtual override returns (bytes[] memory returndata) {
        uint256 callCount = targets.length;
        require(callCount == data.length, "Parameter length mismatch");
        returndata = new bytes[](callCount);
        for (uint256 ind = 0; ind < callCount; ) {
            require(
                targets[ind].code.length > 0,
                "Multicall target not contract"
            );
            bool success;
            // solhint-disable-next-line avoid-low-level-calls
            (success, returndata[ind]) = targets[ind].call(data[ind]);
            if (!success) {
                bytes memory returndataWithRevertData = returndata[ind];
                // Adapted from OpenZeppelin's Address.sol
                if (returndataWithRevertData.length > 0) {
                    // solhint-disable-next-line no-inline-assembly
                    assembly {
                        let returndata_size := mload(returndataWithRevertData)
                        revert(
                            add(32, returndataWithRevertData),
                            returndata_size
                        )
                    }
                } else {
                    revert("Multicall: No revert string");
                }
            }
            unchecked {
                ind++;
            }
        }
    }

    /// @notice Batches calls to external contracts but does not revert if any
    /// of the batched calls reverts
    /// @param targets Array of target addresses of batched calls
    /// @param data Array of calldata of batched calls
    /// @return successes Array of success conditions of batched calls
    /// @return returndata Array of returndata of batched calls
    function tryExternalMulticall(
        address[] calldata targets,
        bytes[] calldata data
    )
        public
        virtual
        override
        returns (bool[] memory successes, bytes[] memory returndata)
    {
        uint256 callCount = targets.length;
        require(callCount == data.length, "Parameter length mismatch");
        successes = new bool[](callCount);
        returndata = new bytes[](callCount);
        for (uint256 ind = 0; ind < callCount; ) {
            if (targets[ind].code.length > 0) {
                // solhint-disable-next-line avoid-low-level-calls
                (successes[ind], returndata[ind]) = targets[ind].call(
                    data[ind]
                );
            } else {
                returndata[ind] = abi.encodeWithSignature(
                    "Error(string)",
                    "Multicall target not contract"
                );
            }
            unchecked {
                ind++;
            }
        }
    }
}

contracts/utils/interfaces/IExpiringMeta.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

interface IExpiringMetaTxForwarder {
    event ExecutedMetaTx(bytes32 indexed metaTxHash);

    event CanceledMetaTx(bytes32 indexed metaTxHash);

    struct ExpiringMetaTx {
        address from;
        address to;
        bytes data;
        uint256 expirationTimestamp;
    }

    function execute(
        ExpiringMetaTx calldata metaTx,
        bytes calldata signature
    ) external returns (bytes memory returndata);

    function cancel(ExpiringMetaTx calldata metaTx) external;

    function metaTxWithHashIsExecutedOrCanceled(
        bytes32 metaTxHash
    ) external returns (bool);
}

