contracts/utils/mock/MockCallForwarderTa.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

contract MockCallForwarderTarget {
    string public storage1;
    uint256 public storage2;

    function payableTargetFunction(
        string calldata input1,
        uint256 input2,
        uint256 msgValue
    ) external payable returns (bytes memory output1, bool output2) {
        require(
            keccak256(abi.encodePacked(input1)) ==
                keccak256(abi.encodePacked("input1")),
            "Incorrect input"
        );
        require(input2 == 123, "Incorrect input");
        require(msg.value == msgValue, "Incorrect value");
        storage1 = input1;
        storage2 = input2;
        output1 = hex"12345678";
        output2 = true;
    }

    function nonpayableTargetFunction(
        string calldata input1,
        uint256 input2
    ) external returns (bytes memory output1, bool output2) {
        require(
            keccak256(abi.encodePacked(input1)) ==
                keccak256(abi.encodePacked("input1")),
            "Incorrect input"
        );
        require(input2 == 123, "Incorrect input");
        storage1 = input1;
        storage2 = input2;
        output1 = hex"12345678";
        output2 = true;
    }
}

contracts/utils/mock/MockExpiringMetaTxF.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/metatx/ERC2771Context.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

contract MockExpiringMetaTxForwarderTarget is ERC2771Context, Ownable {
    uint256 public counter = 0;

    constructor(
        address _trustedForwarder,
        address _owner
    ) ERC2771Context(_trustedForwarder) {
        _transferOwnership(_owner);
    }

    function incrementCounter() external onlyOwner returns (uint256) {
        return ++counter;
    }

    function _msgSender()
        internal
        view
        virtual
        override(Context, ERC2771Context)
        returns (address sender)
    {
        return ERC2771Context._msgSender();
    }

    function _msgData()
        internal
        view
        virtual
        override(Context, ERC2771Context)
        returns (bytes calldata)
    {
        return ERC2771Context._msgData();
    }
}

contracts/utils/mock/MockExternalMultica.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "../ExternalMulticall.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

// This contract overrides the multicall functions to demonstrate how they can
// be extended to only allow the owner to execute multicalls. However, we
// only do that in comments because we want to test the vanilla contract.
contract MockExternalMulticall is ExternalMulticall, Ownable {
    function externalMulticall(
        address[] calldata targets,
        bytes[] calldata data
    ) public virtual override returns (bytes[] memory returndata) {
        // _checkOwner();
        return super.externalMulticall(targets, data);
    }

    function tryExternalMulticall(
        address[] calldata targets,
        bytes[] calldata data
    )
        public
        virtual
        override
        returns (bool[] memory successes, bytes[] memory returndata)
    {
        // _checkOwner();
        return super.tryExternalMulticall(targets, data);
    }
}

contracts/utils/mock/MockMulticallTarget.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.4;

contract MockMulticallTarget {
    error MyError(uint256 fieldAlways123, string fieldAlwaysFoo);

    int256[] private _argumentHistory;

    function alwaysRevertsWithString(
        int256 argPositive,
        int256 argNegative
    ) external pure {
        require(argPositive > 0 && argNegative < 0, "Invalid argument");
        revert("Reverted with string");
    }

    function alwaysRevertsWithCustomError(
        int256 argPositive,
        int256 argNegative
    ) external pure {
        require(argPositive > 0 && argNegative < 0, "Invalid argument");
        revert MyError(123, "Foo");
    }

    function alwaysRevertsWithNoData(
        int256 argPositive,
        int256 argNegative
    ) external pure {
        require(argPositive > 0 && argNegative < 0, "Invalid argument");
        revert(); // solhint-disable-line reason-string
    }

    function convertsPositiveArgumentToNegative(
        int256 argPositive
    ) external returns (int256) {
        require(argPositive > 0, "Argument not positive");
        _argumentHistory.push(argPositive);
        return -argPositive;
    }

    function argumentHistory() external view returns (int256[] memory) {
        int256[] memory argumentHistoryInMemory = new int256[](
            _argumentHistory.length
        );
        for (uint256 ind = 0; ind < _argumentHistory.length; ind++) {
            argumentHistoryInMemory[ind] = _argumentHistory[ind];
        }
        return argumentHistoryInMemory;
    }
}

